import React from 'react';
import { withRouter } from 'react-router-dom';
import { setUserSession } from './Common';
import './Login.css';
import { FormErrors } from './FormErrors';
import axios from 'axios';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      email: '',
      password: '',
      UserType: '',
      RegisterUserType: '',
      // formErrors: { email: '', password: '' },
      emailErrorMsg:{ email: ''},
      passwordErrorMsg:{ password: ''},
      emailValid: false,
      passwordValid: false,
      formValid: false,
    }
  }

  handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value },
      () => { this.validateField(name, value) });
  }

  onValueChange = (e) => {
    this.setState({
      UserType: e.target.value
    })
  }

  validateField(fieldName, value) {
    // let fieldValidationErrors = this.state.formErrors;
    let emailValidationErrorMsg = this.state.emailErrorMsg;
    let passwordValidationErrorMsg = this.state.passwordErrorMsg;
    let emailValid = this.state.emailValid;
    let passwordValid = this.state.passwordValid;

    switch (fieldName) {
      case 'email':
        emailValid = value.length > 0 && value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,3})$/i);
        emailValidationErrorMsg.email = emailValid ? '' : ' is invalid';
        break;
      case 'password':
        if (value.length < 6) {
          passwordValidationErrorMsg.password = ' length is less than 6';
        } else if (value.length > 10) {
          passwordValidationErrorMsg.password = ' accepts max 10';
        } else {
        passwordValid = value.match(/^([\w.%+-])$/i) && value.length <= 10;
        passwordValidationErrorMsg.password = passwordValid ? '' : ' is too short';
        }
        break;
      default:
        break;
    }
    this.setState({
      emailErrorMsg: emailValidationErrorMsg,
      passwordErrorMsg: passwordValidationErrorMsg,
      emailValid: emailValid,
      passwordValid: passwordValid
    }, this.validateForm);
  }

  validateForm() {
    this.setState({ formValid: this.state.emailValid && this.state.passwordValid });
  }

  errorClass(error) {
    return (error.length === 0 ? '' : 'has-error');
  }

  handleSubmit = (e) => {
    var apiBaseUrl = "http://localhost:8000/api/authentication/";
    var self = this;
    var payload = {
      "email": this.state.email,
      "password": this.state.password,
      "role": this.state.UserType
    }

    axios.post(apiBaseUrl + 'login', payload)
      .then(function (response) {

        self.setState({users:response.data});
        setUserSession(response.data[0].id, response.data[0].name, response.data[0].address);
        if (response.status === 200) {
          console.log("Login successfull");

          if (payload.role === "student") {
            self.props.history.push('/dashboardC');          
          }
          else
            if (payload.role === "faculty") {
              self.props.history.push('/dashboardE');
            }


        }
        else if (response.data.code === 204) {
          console.log("emailid and pwd  do not match");
          alert("emailid and pwd  do not match")
        }
        else {
          console.log("User does not exists");
          alert("User does not exist");
        }
      })
      .catch(function (error) {
              console.log(error);
      });
       e.preventDefault();

  }

  render() {
    return (
      <div className="loginContainer">
        <div className="login-menu">
         <h1>Sign In</h1> 
          <form className="demoForm" onSubmit={this.handleSubmit}>

            <div >
              <input type="email" required name="email"
                placeholder="Email"
                value={this.state.email}
                onChange={this.handleUserInput} />
            </div>
            <div className='error-message' >
              <FormErrors formErrors={this.state.emailErrorMsg} />
            </div>
            <div >
              <input type="password" name="password"
                placeholder="Password"
                value={this.state.password}
                onChange={this.handleUserInput} />
            </div>
            <div className='error-message' >
              <FormErrors formErrors={this.state.passwordErrorMsg} />
            </div>
            <div >
              <input type="radio" value="student"
                checked={this.state.UserType === "student"}
                onChange={this.onValueChange} />
              <label htmlFor="Student">Student</label>
              <input type="radio" value="faculty" checked={this.state.UserType === "faculty"}
                onChange={this.onValueChange} />
              <label htmlFor="Faculty">Faculty</label>
            </div>
            <div className="register-menu">
            <div>
              <a href='/registration'>New User? Register Now</a>
            </div>
          </div>
            <input type='submit' value='Login' disabled={!this.state.formValid} />
          </form>
          </div>  
      </div>
    )
  }

}
export default withRouter(Login);
