import React from 'react';
import { getUser, removeUserSession } from './Common';
import { withRouter } from "react-router-dom";
import BookingsTable from "./Table";
import './dashboard.css';
import Jobs from './jobs.js';

class Facultydashboard extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      // currentbooking: this.props.location.state.detail,
      tableData: [
        {
          Course_ID: "WEB01",
          Course_Name: "Web Developmnt",
          End_Date: "2021-04-30",
          Modules: ["Web Application", "UI"],
          Add_on_Topic:'Add',
          Students_Enrolled:"View Details"

        },
        {
          Course_ID: "PYT01",
          Course_Name: "Python",
          End_Date: "2021-06-30",
          Modules: ["Introduction to Python"],
          Add_on_Topic:'Add',
          Students_Enrolled:"View Details"

        },
        {
          Course_ID: "RCT01",
          Course_Name: "React JS",
          End_Date: "2021-02-30",
          Modules: [],
          Add_on_Topic:'Add',
          Students_Enrolled:"View Details"

        }
      ]
    }
    this.handleClick = this.handleClick.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleClick = (e) => {
    this.props.history.push('/CreateService');
    console.log("hi in cre");
    e.preventDefault();
  }

  handleLogout = (e) => {
    removeUserSession();
    this.props.history.push('/');
    e.preventDefault();
  }

  render() {
    const user = getUser();
    if(user.name.length===0){
      alert("Session Lost. Redirecting to login page!");
      this.props.history.push('/');
    } 
    return (
      <div className="dashboard-container">
        <div className="Greeting">
          <h3>Welcome {user.name}</h3>
        </div>
        <div className="Jobs">                  
          {/* <Jobs role="Customer" history={this.props.history} /> */}
          <BookingsTable data={this.state.tableData} />
        </div>
        {/* <div className="search-container">
          <fieldset>
            <legend>Book a Service</legend>
            <input type='submit' value='Book' onClick={this.handleClick} />
          </fieldset>

        </div> */}
        <div className='user-menu'>
          <input type="button" onClick={this.handleLogout} value="Logout" />
        </div>
      </div>
    );

  }
}

export default withRouter(Facultydashboard);
