import React from 'react';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import { getUser, getCourse } from './Common';
import './dashboard.css';
class createModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Courses: [
        {
          Course_ID: "WEB01",
          Course_Name: "Web Developmnt",
          End_Date: "2021-04-30",

        },
        {
          Course_ID: "PYT01",
          Course_Name: "Python",
          End_Date: "2021-06-30",

        },
        {
          Course_ID: "RCT01",
          Course_Name: "React JS",
          End_Date: "2021-02-30",
        }],
      moduleName: '',
      description: '',
      topics: [],
      enableSubmit:false
    };
  }

  handleSubmit = (e) => {
    alert("Created Successfully!");
  }
  handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    let buttonValidate = this.enableButton;
    if (name === "courses") {
      buttonValidate = true;
    }
    this.setState({ [name]: value, enableSubmit: buttonValidate});
  }
  handleGoBack = (e) => {
    this.props.history.push('/dashboardF');
    e.preventDefault();
  }

  render() {
    return (
      <div name = "dashboard-container">
        <fieldset>
          <legend>Create Module</legend>

          <div className="back">
        <input type="submit" onClick={this.handleGoBack} value="GoBack"/>
        </div>
        </fieldset>
      </div>
    );
  }

}

export default withRouter(createModule);
