import React from 'react';
import { getUser, removeUserSession } from './Common';
import { Redirect, withRouter } from "react-router-dom";
import FacultyTable from "./Table";
import './dashboard.css';
import { setUserSession } from './Common';
import axios from 'axios';

class Facultydashboard extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      tableData: []
    }
    this.handleLogout = this.handleLogout.bind(this);
  }


  handleLogout = (e) => {
    removeUserSession();
    this.props.history.push('/');
    e.preventDefault();
  }


  fetchCourseAssignedDetails () {
    var apiBaseUrl = "http://localhost:8080/facultyDetails/"+this.state.email;
    var self = this;
    var payload = {
      "id": this.state.email,
      "password": this.state.password,
      "role": this.state.UserType
    }
    var username = this.state.email.substring(0,this.state.email.indexOf('@'));

    axios.get(apiBaseUrl, payload)
      .then(function (response) {

        if (response.status === 200) {
          self.setState({user:username}, () => {
            setUserSession(self.state.email, username, self.state.UserType);
            alert("Login Successful!");
            if (self.state.UserType ==='faculty') {
            self.props.history.push('/LMS/dashboardF');
        } else {
          alert("Student Dashboard not available!");
        }
      });

        }
      }).catch(function (error) {
        console.log("Invalid Username and Password!");
        alert("Invalid Username and Password!");
      });
       e.preventDefault();    

  }

  render() {
    let user;
    user = getUser();

    let bodycontent;
    if(user.role!=="faculty"){
      bodycontent = <label>No Course</label>
    } else {
      bodycontent = <FacultyTable data={this.state.tableData} />
    }
    return (
      <div className="dashboard-container">
        <div className="Greeting">
          <h3>Welcome {user.name}</h3>
        </div>
        <div className="ViewTitle">
          <h1>FACULTY USER DASHBOARD</h1>
        </div>
        <div className="course">  
        <h2>List of assigned Courses:</h2>                
          {bodycontent}
        </div>

        <div className='user-menu'>
        <input type="submit" onClick={this.handleLogout} value="Sign out"/>
        </div>
      </div>
    );

  }
}
export default withRouter(Facultydashboard);
