import React from 'react';
import './Registration.css'
import { FormErrors } from './FormErrors';
import axios from 'axios';
class Registration extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      password: '',
      dob:'',
      role: '',
      skills: [],
      emailErrorMsg:{ email: ''},
      dobErrorMsg:{ dob: ''},
      passwordCriteria:{ password: ''},
      emailValid: false,
      passwordValid: false,
      dobValid: false,
      roleValid: false,
      RegistrationEnable: false,
    }
  }
  handleChange = (e) => {
    this.setState({
      role: e.target.value
    })
  }

  pwdCriteriaOnClick = (e)=>{
    let pwdCriteria = this.state.passwordCriteria;
    pwdCriteria.password=' length should be min 6 to max 10';
    this.setState({passwordCriteria:pwdCriteria});
  }
  emptyPwdCriteriaOnChange = (e)=>{
    let pwdCriteria = this.state.passwordCriteria;
    pwdCriteria.password='';
    this.setState({passwordCriteria:pwdCriteria});
  }

  validateField(fieldName, value) {
    let emailValidationErrorMsg = this.state.emailErrorMsg;
    let dobValidationErrorMsg = this.state.dobErrorMsg;
    let emailValid = this.state.emailValid;
    let passwordValid = this.state.passwordValid;
    let userTypeValid = this.state.userTypeValid;
    let phonevalid = this.state.phonevalid;
    let userTypeValue = this.state.UserType;

    switch (fieldName) {
      case 'email':
        emailValid = value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,3})$/i);
        fieldValidationErrors.email = emailValid ? '' : ' is invalid';
        break;
      case 'password':
        passwordValid = value.length >= 6;
        fieldValidationErrors.password = passwordValid ? '' : ' is too short';
        break;
      case 'phone':
        phonevalid = value.match(/^[0-9]{10}$/i);
        fieldValidationErrors.phone = phonevalid ? '' : 'is not valid';
        break;
      default:
        break;
    }
    this.setState({
      formErrors: fieldValidationErrors,
      emailValid: emailValid,
      passwordValid: passwordValid,
      phonevalid: phonevalid
    }, this.validateForm);
  }

  validateRegistration() {
    this.setState({ RegistrationEnable: this.state.emailValid && this.state.dob });
  }

  errorClass(error) {
    return (error.length === 0 ? '' : 'has-error');
  }

  handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value },
      () => { this.validateField(name, value) });
  }

  handleSubmit = (e) => {
    var apiBaseUrl = "http://localhost:8000/api/authentication/";
    var self = this;
    var payload = {
      "name": this.state.name,
      "email": this.state.email,
      "password": this.state.password,
      "dob": this.state.dob,
      "role": this.state.role,
    }

    axios.post(apiBaseUrl + 'registration', payload)
      .then(function (response) {
        console.log(response);
        if (response.status === 201) {
          alert("Registration successfull.Login Again");
          console.log("Registration successfull");
          self.props.history.push('/');
        }
        else if (response.data.code === 204) {
          console.log("invalid data");
          alert("invalid data")
        }
        else {
          console.log("User  exists");
          alert("User  exist");
        }

      })
      .catch(function (error) {
        console.log("")
        console.log(error);
      });

    e.preventDefault();

  }

  render() {
      return (
        <div className="registrationContainer">
          <form onSubmit={this.handleSubmit}>
            <fieldset>
              <legend>Registration Form</legend>
              <div className="UserRole">

                <input type="radio" value="student"
                  checked={this.state.role === "student"}
                  onChange={this.handleUserInput} />
                <label htmlFor="Student">Student</label>

                <input type="radio" value="faculty" checked={this.state.role === "faculty"}
                  onChange={this.handleUserInput} />
                <label htmlFor="Faculty">Faculty</label>
              </div>

              <div className="UserDetails">

                <input
                  type="text"
                  placeholder="Enter your email id [username]*"
                  name="email"
                  value={this.state.email}
                  onChange={this.handleUserInput}
                  required
                />
             <FormErrors formErrors={this.state.emailErrorMsg} />
                <input
                  type="password"
                  placeholder="Enter Password *"
                  name="password"
                  value={this.state.password}
                  onSelect={this.pwdCriteriaOnClick}
                  onChange= {this.emptyPwdCriteriaOnChange}
                  required
                />
              <FormErrors formErrors={this.state.passwordCriteria} />
               <input
                  type="text"
                  placeholder="Enter your full name *"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleUserInput}
                  required
                />

              <input
                type="text"
                placeholder="Enter Date-Of-Birth (dd/mm/yyyy) *"
                name="dob"
                value={this.state.dob}
                onChange={this.handleUserInput}
                required
              />
              <FormErrors formErrors={this.state.dobErrorMsg} />
              <input type='submit' value='Submit' disabled={!this.state.RegistrationEnable} />
              </div>
              {/* <div className='error-message' >
                <FormErrors formErrors={this.state.formErrors} />
              </div> */}

            </fieldset>
          </form>
        </div>
      );

  }
}


export default Registration;
