import React from 'react';
import './Registration.css'
import { FormErrors } from './FormErrors';
import axios from 'axios';
class Registration extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      FullName: '',
      Email: '',
      Password: '',
      dob:'',
      role: '',
      skills: [],
      emailErrorMsg:{ email: ''},
      dobErrorMsg:{ dob: ''},
      usernameErrorMsg:{ name: ''},
      passwordCriteria:{ password: ''},
      passwordErrorMsg:'',
      emailValid: false,
      passwordValid: false,
      dobValid: false,
      roleValid: false,
      usernameValid:false,
      RegistrationEnable: false,
    }
  }
  handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value },
      () => { this.validateField(name, value) });
  }

  pwdCriteriaOnClick = (e)=>{
    let pwdCriteria = this.state.passwordCriteria;
    pwdCriteria.password=' length should be min 6 to max 10';
    this.setState({passwordCriteria:pwdCriteria});
  }

  validateField(fieldName, value) {
    let emailValidationErrorMsg = this.state.emailErrorMsg;
    let dobValidationErrorMsg = this.state.dobErrorMsg;
    let usernameValidationErrorMsg = this.state.usernameErrorMsg;
    let emailValid = this.state.emailValid;
    let roleValid = this.state.roleValid;
    let dobValid = this.state.dobValid;
    let usernameValid = this.state.usernameValid;
    let pwdValid = this.state.passwordValid;
    let pwdError = this.state.passwordErrorMsg;
    let pwdCriteria = this.state.passwordCriteria;

    switch (fieldName) {
      case 'email':
        if (value.length===0) {
          emailValid = false;
          emailValidationErrorMsg.email = '';
        } else if (value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,3})$/i)) {
          emailValid = true;
          emailValidationErrorMsg.email = '';
        } else {
          emailValid = false;
          emailValidationErrorMsg.email = ' is invalid!';
        }
        break;
      case 'dob':
        if(value.length===10){
          dobValid = true;
          dobValidationErrorMsg.dob = '';
        } else if (value.length===0) {
          dobValid=false;
          dobValidationErrorMsg.dob = '';
        } else {
          dobValid=false;
          dobValidationErrorMsg.dob = ' invalid date format!';
        }
        break;
        case 'name':
          if (value.length===0) {
            usernameValid=false;
            usernameValidationErrorMsg.name = '';
          } else {
            usernameValid=true;
            usernameValidationErrorMsg.name = '';
          }
          break; 
        case 'password':
            //Password error message creation
            if (value.length > 0 && value.length < 6) {
              pwdValid=false;
              pwdError = 'Password length is less than 6';
              } else if (value.length > 10) {
            pwdValid=false;
            pwdError = 'Password accepts max 10';
          } else if (value.length===0) {
            pwdValid=false;
            pwdError ='';
          } else {
            pwdValid=true;
            pwdError ='';
            pwdCriteria.password='';
          }
          break; 
      default:
        // radio button selection for user selection
        roleValid = true;
        break;
    }
    this.setState({
      emailErrorMsg: emailValidationErrorMsg,
      emailValid: emailValid,
      dobValid: dobValid,
      dobErrorMsg: dobValidationErrorMsg,
      roleValid: roleValid,
      usernameValid: usernameValid,
      usernameErrorMsg: usernameValidationErrorMsg,
      passwordErrorMsg: pwdError, 
      passwordValid: pwdValid,
      passwordCriteria: pwdCriteria,

    }, this.validateRegistration);
  }

  validateRegistration() {
    this.setState({ RegistrationEnable: this.state.emailValid && this.state.dobValid && this.state.usernameValid && this.state.roleValid });
  }

  errorClass(error) {
    return (error.length === 0 ? '' : 'has-error');
  }


  handleSubmit = (e) => {
    var apiBaseUrl = "http://localhost:8000/api/authentication/";
    var self = this;
    var payload = {
      "name": this.state.FullName,
      "email": this.state.Email,
      "password": this.state.Password,
      "dob": this.state.dob,
      "role": this.state.role,
    }

    axios.post(apiBaseUrl + 'registration', payload)
      .then(function (response) {
        console.log(response);
        if (response.status === 201) {
          alert("Registration successfull.Login Again");
          console.log("Registration successfull");
          self.props.history.push('/');
        }
        else if (response.data.code === 204) {
          console.log("invalid data");
          alert("invalid data")
        }
        else {
          console.log("User  exists");
          alert("User  exist");
        }

      })
      .catch(function (error) {
        console.log("")
        console.log(error);
      });

    e.preventDefault();

  }

  render() {
      return (
        <div className="registrationContainer">
          <form onSubmit={this.handleSubmit}>
            <fieldset>
              <legend>Registration Form</legend>
              <div className="UserRole">

                <input type="radio" value="student"
                  checked={this.state.role === "student"}
                  onChange={this.handleUserInput} />
                <label htmlFor="Student">Student</label>

                <input type="radio" value="faculty" checked={this.state.role === "faculty"}
                  onChange={this.handleUserInput} />
                <label htmlFor="Faculty">Faculty</label>
              </div>

              <div className="UserDetails">

                <input
                  type="text"
                  placeholder="Enter your email id [username]*"
                  name="email"
                  value={this.state.email}
                  onChange={this.handleUserInput}
                  required
                />
             <FormErrors formErrors={this.state.emailErrorMsg} />

                <input
                  type="password"
                  placeholder="Create Password *"
                  name="password"
                  value={this.state.password}
                  onSelect={this.pwdCriteriaOnClick}
                  onChange= {this.handleUserInput}
                  required
                />
              <FormErrors formErrors={this.state.passwordCriteria} />

               <input
                  type="text"
                  placeholder="Enter your full name *"
                  name="name"
                  value={this.state.name}
                  onChange={this.handleUserInput}
                  required
                />

              <input
                type="text"
                placeholder="Enter Date-Of-Birth (dd/mm/yyyy) *"
                name="dob"
                value={this.state.dob}
                onChange={this.handleUserInput}
                required
              />
              <FormErrors formErrors={this.state.dobErrorMsg} />
              <input type='submit' value='Submit' disabled={!this.state.RegistrationEnable} />
              </div>

            </fieldset>
          </form>
        </div>
      );

  }
}


export default Registration;
