import React from 'react';
import { getUser, removeUserSession } from './Common';
import { withRouter } from "react-router-dom";
import BookingsTable from "./Table";
import './dashboard.css';
import Jobs from './jobs.js';

class FacultyDashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // currentbooking: this.props.location.state.detail,
      facultyTableData: [
        {
          CourseID: "WA1",
          CourceName: "Web Developmnt",
          Modules: [{modulename:"Web Application", description:"Client Server Model", topics:["Web App Architecture", "Layered Architecture"]},
                    {modulename:"UI", description:"Hypertext Markup Language (HTML)", topics:["HTML Elements", "HTML Forms"]}],
          EndDate: "2021-02-30",
        },
        {
          CourseID: "PY01",
          CourceName: "Python",
          Modules: [{modulename:"Introduction to Python", description:"Explain core concept of python ", topics:["Variables & Datatypes", "Functions"]}],
          EndDate: "2021-08-30"
        }
      ]
    }
    this.handleClick = this.handleClick.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleClick = (e) => {
    this.props.history.push('/CreateModule');
    e.preventDefault();
  }

  handleLogout = (e) => {
    removeUserSession();
    this.props.history.push('/');
    e.preventDefault();
  }

  render() {
    // const usertype = this.props.role;
    // if (usertype === 'Faullty') {

    // }
    return (

      <div className="dashboard-container">
        <div className="Greeting">
          <h3>Welcome {user.name} !</h3>
        </div>
        <div className="courses">                  
          <BookingsTable data={this.state.tableData} />
        </div>
        {/* <div className="search-container">
          <fieldset>
            <legend>Book a Service</legend>
            <input type='submit' value='Book' onClick={this.handleClick} />
          </fieldset>

        </div> */}
        <div className='user-menu'>
          <input type="button" onClick={this.handleLogout} value="Logout" />
        </div>
      </div>
    );
  }
}

export default withRouter(FacultyDashboard);
