// return the user data from the session storage
export const getUser = () => {
    const userStr= [];
     userStr.email = sessionStorage.getItem('email');
     userStr.name = sessionStorage.getItem('user');
     userStr.role = sessionStorage.getItem('role');
    return userStr;
   }


  // remove the token and user from the session storage
  export const removeUserSession = () => {
    sessionStorage.removeItem('email');
    sessionStorage.removeItem('user');
    sessionStorage.removeItem('role');

  }

  // set the token and user from the session storage
  export const setUserSession = (email, name, userType) => {
    sessionStorage.setItem('email', email);
    sessionStorage.setItem('user', JSON.stringify(name));
    sessionStorage.setItem('role', JSON.stringify(userType));

  }
