import React from 'react';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import { getUser } from './Common';
import './dashboard.css';
class createModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Courses: [
        {
          Course_ID: "WEB01",
          Course_Name: "Web Developmnt",
          End_Date: "2021-04-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        },
        {
          Course_ID: "PYT01",
          Course_Name: "Python",
          End_Date: "2021-06-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        },
        {
          Course_ID: "RCT01",
          Course_Name: "React JS",
          End_Date: "2021-02-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        }],
      moduleName: '',
      description: '',
      topics: [],
      enableSubmit:false
    };
  }

  handleSubmit = (e) => {
    alert("Created Successfully!");
  }
  handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    const buttonValidate = this.enableButton;
    if (name === "courses") {
      buttonValidate = true;
    }
    this.setState({ [name]: value, enableSubmit: buttonValidate});
  }

  render() {
    return (
      <div name = "moduleContainer">
        <fieldset>
          <legend>Create Module</legend>
          <form
            name="ModuleDetails"
            onSubmit={this.handleSubmit}
            method="post"
          >
            <select
            placeholder="Select Course"
            name = "courses"
            required
              onChange={this.handleUserInput}
            >
              {this.state.Courses.map((course) => <option key={course.Course_ID} value={({key:course.Course_ID, value:course.Course_Name})}>{course.Course_Name}</option>)}
            </select>
            <br />

            <input
                  type="text"
                  placeholder="Enter module name*"
                  name="moduleName"
                  value={this.state.moduleName}
                  onChange={this.handleUserInput}
                  required
                />

                <textarea
                  placeholder="Enter description*"
                  name="description"
                  value={this.state.description}
                  onChange= {this.handleUserInput}
                  required
                />

               <input
                  type="text"
                  placeholder="Enter your full name *"
                  name="FullName"
                  value={this.state.FullName}
                  onChange={this.handleUserInput}
                  required
                />

              <input
                type="text"
                placeholder="Enter Date-Of-Birth (dd/mm/yyyy) *"
                name="dob"
                value={this.state.dob}
                onChange={this.handleUserInput}
                required
              />
            <br />
            <input type='submit' value='Submit' disabled={!enableButton}/>

          </form>
        </fieldset>
      </div>
    );
  }

}

export default withRouter(createModule);
