import React from 'react';
import { getUser, removeUserSession } from './Common';
import { withRouter } from "react-router-dom";
import FacultyTable from "./Table";
import './dashboard.css';
import Jobs from './jobs.js';

class Facultydashboard extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      tableData: [
        {
          Course_ID: "WEB01",
          Course_Name: "Web Developmnt",
          End_Date: "2021-04-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        },
        {
          Course_ID: "PYT01",
          Course_Name: "Python",
          End_Date: "2021-06-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        },
        {
          Course_ID: "RCT01",
          Course_Name: "React JS",
          End_Date: "2021-02-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        }
      ],
    }
    this.handleClick = this.handleClick.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleClick = (e) => {
    this.props.history.push('/CreateService');
    console.log("hi in cre");
    e.preventDefault();
  }

  handleLogout = (e) => {
    removeUserSession();
    this.props.history.push('/');
    e.preventDefault();
  }

  render() {
    const user = getUser();
    if(user.name.length===0){
      alert("Session Lost. Redirecting to login page!");
      this.handleLogout();
    } 


    if (user.name==='karthikeyan') {
      return (
        <div className="dashboard-container">
          <div className="Greeting">
            <h3>Welcome {user.name}</h3>
          </div>
          <div className="course">  
          <h2>List of assigned courses:</h2>                
            {/* <Jobs role="Customer" history={this.props.history} /> */}
            <FacultyTable data={this.state.tableData} />
          </div>

          <div className='user-menu'>
            <a href={this.handleLogout()}>Sign Out</a>
          </div>
        </div>
      )
    } else {
      return (
        <div className="dashboard-container">
          <div className="Greeting">
            <h3>Welcome {user.name}</h3>
          </div>
          <div className="course">  
          <h2>List of assigned courses:</h2>                
          <label>no courses</label>          
          </div>

          <div className='user-menu'>
            <a href={this.handleLogout()}>Sign Out</a>
          </div>
        </div>
      )
    }


  }
}

export default withRouter(Facultydashboard);
