import React from 'react';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import { getUser } from './Common';
import './dashboard.css';
class createModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Courses: [
        {
          Course_ID: "WEB01",
          Course_Name: "Web Developmnt",
          End_Date: "2021-04-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        },
        {
          Course_ID: "PYT01",
          Course_Name: "Python",
          End_Date: "2021-06-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        },
        {
          Course_ID: "RCT01",
          Course_Name: "React JS",
          End_Date: "2021-02-30",
          Add_Module:'Add',
          Students_Enrolled:"View Details"

        }],
      moduleName: '',
      description: '',
      topics: []
    };
  }

  handleSubmit = (e) => {
    alert("Created Successfully!");
  }
  handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <div>
        <fieldset>
          <legend>Create Module</legend>
          <form
            name="ModuleDetails"
            onSubmit={this.handleSubmit}
            method="post"
          >
            <select
              onChange={this.fetchServices}
            >
              {this.state.Courses.map((course) => <option key={course.Course_ID} value={({key:course.Course_ID, value:course.Course_Name})}>{course.Course_Name}</option>)}
            </select>
            <br />

            <input type='text'
              name='description'
              value={this.state.description}
              placeholder="Description"
              maxLength={1155}
              onChange={this.handleUserInput}

            />
            <div>
                <input type="date" id="start" name="trip-start"
                onChange={this.onChangedate}
                value={this.state.start_date}
                min="2018-01-01" max="2028-12-31"></input>
             <input type="time" id="appt" name="appt"
             onChange={this.onChangetime}
             value={this.state.start_time}
             ></input>
            </div>
            <br />
            <input type='submit' value='Submit' />

          </form>
        </fieldset>
      </div>
    );
  }

}

export default withRouter(createModule);
