import React from 'react';
import { getUser, removeUserSession } from './Common';
import { withRouter } from "react-router-dom";
import FacultyTable from "./Table";
import './dashboard.css';

class CreateModule extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      tableData: [
        {
          Student_ID:'202001',
          Student_Name:'Karthik',
          Student_EmailID:'Karhtik@gmail.com',
          Student_PhoneNo:'9762567373'

        },
        {
          Student_ID:'202002',
          Student_Name:'Shankar',
          Student_EmailID:'Shankar@gmail.com',
          Student_PhoneNo:'8939737733'
        },
        {
          Student_ID:'202003',
          Student_Name:'Bharat',
          Student_EmailID:'Bharat@gmail.com',
          Student_PhoneNo:'9891872773'
        },
        {
          Student_ID:'202004',
          Student_Name:'Raj',
          Student_EmailID:'Raj@gmail.com',
          Student_PhoneNo:'9787873287'
        }
      ],
    }
    this.handleLogout = this.handleLogout.bind(this);

  }



  handleGoBack = (e) => {
    this.props.history.push('/dashboardF');
    e.preventDefault();
  }


  handleLogout = (e) => {
    removeUserSession();
    this.props.history.push('/');
    e.preventDefault();
  }

  render() {
    const user = getUser();
    if(user.name.length===0){
      alert("Session Lost. Redirecting to login page!");
      this.handleLogout();
    } 
    let bodycontent;

    return (
      <div className="dashboard-container">
        <div className="Greeting">
          <h3>Welcome {user.name}</h3>
        </div>
        <div className="course">  
        <h2>Create Module:</h2>                
        <form
            name="ModuleDetails"
            onSubmit={this.handleSubmit}
            method="post"
          >
            <select
            placeholder="Select Course"
            name = "courses"
              onChange={this.handleUserInput}
              required
            >
              {this.state.Courses.map((course) => <option key={course.Course_ID} value={({key:course.Course_ID, value:course.Course_Name})}>{course.Course_Name}</option>)}
            </select>
            <br />

            <input
                  type="text"
                  placeholder="Enter module name*"
                  name="moduleName"
                  value={this.state.moduleName}
                  onChange={this.handleUserInput}
                  required
                />

                <textarea
                  placeholder="Enter description*"
                  name="description"
                  value={this.state.description}
                  onChange= {this.handleUserInput}
                  required
                />

                 <textarea
                  placeholder="Enter Topics*"
                  name="topics"
                  value={this.state.topics}
                  onChange= {this.handleUserInput}
                  required
                />
            <br />
            <input type='submit' value='Submit'/>

          </form>
        </div>
        <div className="back">
        <input type="submit" onClick={this.handleGoBack} value="GoBack"/>
        </div>
        <div className='user-menu'>
        <input type="submit" onClick={this.handleLogout} value="Signout"/>
        </div>
      </div>
    );

  }
}

export default withRouter(CreateModule);
